mapper.select:
select id, requested_at as requestedAt, client_ip as clientIp, username, message from op_log
order by ${p.sortColumn} ${p.sortOrder}
limit #{p.offset}, #{p.pageSize}

sql.where:
and requested_at >= concat(#{q.startAt}, ' 00:00:00')
and requested_at <= concat(#{q.endAt}, ' 23:59:59')
and client_ip like #{q.clientIp}
and username like #{q.username}
and message like #{q.message}
